## Binary Classification


import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.metrics import classification_report
from transformers import BertTokenizer, BertForSequenceClassification, AdamW
from torch.utils.data import DataLoader, TensorDataset
import torch
import matplotlib.pyplot as plt



from transformers import BertForSequenceClassification


# Tokenize and preprocess the text
def preprocess_text(texts, tokenizer, max_len=128):
    input_ids = []
    attention_masks = []

    for text in texts:
        encoded_dict = tokenizer.encode_plus(
            text,
            add_special_tokens=True,
            max_length=max_len,
            # pad_to_max_length=True,
            padding = 'max_length' ,
            return_attention_mask=True,
            return_tensors='pt'
        )

        input_ids.append(encoded_dict['input_ids'])
        attention_masks.append(encoded_dict['attention_mask'])

    return torch.cat(input_ids, dim=0), torch.cat(attention_masks, dim=0)

def fine_tune_bert_model(train_dataloader, model, optimizer, epochs, test_dataloader):
    device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
    model.to(device)
    train_losses = []
    accuracies = []
    valid_losses = []
    criterion = torch.nn.CrossEntropyLoss()
    for epoch in range(epochs):
        model.train()
        total_loss = 0
        correct_predictions = 0
        total_predictions = 0
        for batch in train_dataloader:
            b_input_ids, b_input_mask, b_labels = batch
            b_input_ids, b_input_mask, b_labels = b_input_ids.to(device), b_input_mask.to(device), b_labels.to(device)
            optimizer.zero_grad()
            outputs = model(input_ids=b_input_ids, attention_mask=b_input_mask, labels=b_labels)
            loss = outputs.loss
            total_loss += loss.item()
            loss.backward()
            optimizer.step()

            logits = outputs.logits
            _, preds = torch.max(logits, dim=1)
            correct_predictions += torch.sum(preds == b_labels)
            total_predictions += b_labels.size(0)



        avg_loss = total_loss / len(train_dataloader)
        accuracy = correct_predictions.double() / total_predictions
        train_losses.append(avg_loss)
        accuracies.append(accuracy.item())

        # print("Model testing done")

        model.eval()
        all_labels = []
        all_preds = []
        total_valid_loss = 0
        with torch.no_grad():
            for batch in test_dataloader:
                b_input_ids, b_input_mask, b_labels = batch
                b_input_ids, b_input_mask, b_labels = b_input_ids.to(device), b_input_mask.to(device), b_labels.to(device)
                outputs = model(input_ids=b_input_ids, attention_mask=b_input_mask, labels=b_labels)
                logits = outputs.logits
                _, preds = torch.max(logits, dim=1)
                all_labels.extend(b_labels.cpu().numpy())
                all_preds.extend(preds.cpu().numpy())
                loss = criterion(logits, b_labels)
                total_valid_loss += loss.item()

        accuracy = sum(1 for x, y in zip(all_preds, all_labels) if x == y) / len(all_preds)
        valid_losses.append(total_valid_loss / len(test_dataloader))

        print(f'Epoch {epoch + 1}/{epochs}, Loss: {avg_loss}, Accuracy: {accuracy}')

    return train_losses, valid_losses, accuracies[-1]



# Step 1: Load the multiclass CSV dataset
#csv_path = "WIKIHOW.csv"
df = pd.read_csv('WIKIHOW.csv',encoding = 'latin-1')
print(df.head())

# Step 2: Split the dataset into train and test sets
train_texts, test_texts, train_labels, test_labels = train_test_split(
    df['Sentences'], df['Type'], test_size=0.2, random_state=42
    )

# Step 3: Tokenize and preprocess the text
tokenizer = BertTokenizer.from_pretrained('bert-base-uncased')
train_input_ids, train_attention_masks = preprocess_text(train_texts, tokenizer)
test_input_ids, test_attention_masks = preprocess_text(test_texts, tokenizer)

print("Tokenize and preprocess the text")
# Convert labels to tensors
train_labels = torch.tensor(train_labels.values)
test_labels = torch.tensor(test_labels.values)

print("Convert labels to tensors")
# Create DataLoader for training
train_dataset = TensorDataset(train_input_ids, train_attention_masks, train_labels)
train_dataloader = DataLoader(train_dataset, batch_size=32, shuffle=True)
# Create DataLoader for testing
test_dataset = TensorDataset(test_input_ids, test_attention_masks, test_labels)
test_dataloader = DataLoader(test_dataset, batch_size=32, shuffle=False)

print("dataloader done")

# Step 4: Initialize BERT model and optimizer
model = BertForSequenceClassification.from_pretrained('bert-base-uncased', num_labels=len(df['Type'].unique()))
print("model done")
optimizer = AdamW(model.parameters(), lr=2e-5)

# Step 5: Fine-tune BERT model on the training set
# No. of epochs
epochs = 5

# Tuning the model
train_losses ,valid_losses, accuracy = fine_tune_bert_model(train_dataloader, model, optimizer,epochs,test_dataloader)

# Training and validation loss graph
from matplotlib import pyplot as plt
epoch_list = range(1,epochs+1)
fig, ax = plt.subplots()
ax.plot(epoch_list,train_losses, label="Training loss")
ax.plot(epoch_list,valid_losses, label="Validation loss")
ax.set_title("Training and Validation loss")
ax.set_xlabel("Epochs")
ax.set_ylabel("Loss")
ax.legend()
plt.show()

#printing the accuracy of the model.
print("Accuracy: ",accuracy)

# Step 6: Evaluate the model and classification report
def evaluate_model(model, dataloader):
    device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
    model.to(device)
    model.eval()

    all_labels = []
    all_preds = []

    with torch.no_grad():
        for batch in dataloader:
            b_input_ids, b_input_mask, b_labels = batch
            b_input_ids, b_input_mask, b_labels = b_input_ids.to(device), b_input_mask.to(device), b_labels.to(device)
            outputs = model(input_ids=b_input_ids, attention_mask=b_input_mask)
            logits = outputs.logits
            _, preds = torch.max(logits, dim=1)
            all_labels.extend(b_labels.cpu().numpy())
            all_preds.extend(preds.cpu().numpy())

    return all_labels, all_preds
labels, preds = evaluate_model(model, test_dataloader)
print(classification_report(labels, preds))


# Step 7: Save the trained model
model.save_pretrained("model_binary")
